import {useState , createContext} from 'react'

const BebidasContext = createContext()

const BebidasProvider = ({children}) => {

    const juan = 'gordo'

  return (
        <BebidasContext.Provider
          value={{
            juan
          }}
        >
             {children}
        </BebidasContext.Provider>
  )
}

export {BebidasProvider}
export default BebidasContext
